$database = Database::load();\n$sql = "SELECT * FROM test_multipe_refcursor();";\n$p = $database->query($sql);\n\n$results = [];\nwhile ($row = $p->fetch(PDO::FETCH_ASSOC)) {\n    $cursorName = $row['cursor_name'];\n    $fetchCursorSQL = "FETCH ALL IN \"$cursorName\";";\n    $cursorResult = $database->query($fetchCursorSQL);\n    $results[$cursorName] = $cursorResult->fetchAll(PDO::FETCH_ASSOC);\n}\n\n$p->closeCursor();\n\n// Now, $results array contains data from both cursors\nforeach ($results as $cursorName => $data) {\n    echo "Data from $cursorName:\n";\n    print_r($data);\n}