<?php\n// Your YouTube API key\n$apiKey = 'YOUR_API_KEY';\n// The search query\n$query = 'your search query';\n// API URL\n$apiUrl = 'https://www.googleapis.com/youtube/v3/search?part=snippet&q='.urlencode($query).'&type=video&key='.$apiKey;\n\n// Initialize cURL\n$ch = curl_init();\n\n// Set the options\ncurl_setopt($ch, CURLOPT_URL, $apiUrl);\ncurl_setopt($ch, CURLOPT_RETURNTRANSFER, 1);\n\n// Execute the request\n$response = curl_exec($ch);\n\n// Close cURL\ncurl_close($ch);\n\n// Decode the JSON response\n$data = json_decode($response);\n\n// Check for errors\nif (isset($data->error)) {\n    echo 'Error: '.$data->error->message;\n} else {\n    // Process the results\n    foreach ($data->items as $item) {\n        echo 'Title: '.$item->snippet->title.'<br>';\n        echo 'Description: '.$item->snippet->description.'<br>';\n        echo 'Video ID: '.$item->id->videoId.'<br>';\n        echo '--------------------------<br>';\n    }\n}\n?>