// Load the PNG image\n$image = ImageCreateFromPNG('path/to/your/image.png');\n\n// Create a new true color image with the same dimensions\n$width = imagesx($image);\n$height = imagesy($image);\n$new_image = imagecreatetruecolor($width, $height);\n\n// Fill the new image with a white background\n$white = imagecolorallocate($new_image, 255, 255, 255);\nimagefill($new_image, 0, 0, $white);\n\n// Copy the PNG image onto the new image\nimagecopy($new_image, $image, 0, 0, 0, 0, $width, $height);\n\n// Save the image as a JPEG\nimagejpeg($new_image, 'path/to/your/image.jpg', 100);\n\n// Free up memory\nimagedestroy($image);\nimagedestroy($new_image);