<?php\nif (isset($_GET['referer']) && isset($_GET['end'])) {\n    $referer = filter_var($_GET['referer'], FILTER_SANITIZE_URL);\n    $end = filter_var($_GET['end'], FILTER_SANITIZE_URL);\n\n    if (filter_var($referer, FILTER_VALIDATE_URL) && filter_var($end, FILTER_VALIDATE_URL)) {\n        ?>\n        <html>\n        <head>\n            <meta http-equiv="refresh" content="0;url=<?php echo htmlspecialchars($end); ?>">\n            <script type="text/javascript">\n                document.referrer = "<?php echo htmlspecialchars($referer); ?>";\n                window.location.href = "<?php echo htmlspecialchars($end); ?>";\n            </script>\n        </head>\n        <body>\n        </body>\n        </html>\n        <?php\n    } else {\n        echo "Invalid URL provided.";\n    }\n} else {\n    echo "Parameters 'referer' and 'end' are required.";\n}\n?>