function getAll($id) {\n\n    // Explicitly select the required columns\n    $this->db->select('movies.title, movies.year, movies.runtime, movies.plotoutline, posters.poster_url');\n    $this->db->from('movies');\n    $this->db->join('posters', 'movies.id = posters.id');\n    $this->db->where('movies.id', $id);  // Add the where clause\n\n    $q = $this->db->get();\n\n    if ($q->num_rows() == 1) {\n        $row = $q->row();\n        $data = array(\n            'title' => $row->title,\n            'year' => $row->year,\n            'runtime' => $row->runtime,\n            'plotoutline' => $row->plotoutline,\n            'poster_url' => $row->poster_url\n        );\n    }\n\n    $q->free_result();\n    return $data;\n}