<?php\nfunction formatVariable($variable, $format) {\n    $result = '';\n    $variableIndex = 0;\n\n    for ($i = 0; $i < strlen($format); $i++) {\n        if ($format[$i] === 'x' && isset($variable[$variableIndex])) {\n            $result .= $variable[$variableIndex];\n            $variableIndex++;\n        } elseif ($format[$i] !== 'x') {\n            $result .= $format[$i];\n        }\n    }\n\n    return $result;\n}\n\n// Example usage\n$variable = "123456";\n$format = "xxx-xxx";\necho formatVariable($variable, $format); // Output: 123-456\n\n$variable = "1234";\n$format = "x-xxx";\necho formatVariable($variable, $format); // Output: 1-234\n\n$variable = "1234567890";\n$format = "x-xx-xxx-xxxx";\necho formatVariable($variable, $format); // Output: 1-23-456-7890\n?>