// Database connection details\n$databases = [\n    'SGopen' => ['localhost', 'root', '', 'sGopen'],\n    'PTsecond' => ['localhost', 'root', '', 'pTsecond'],\n    // Add other databases here\n];\n\n// Function to connect to a database\nfunction connectToDatabase($host, $user, $password, $dbname) {\n    $conn = new mysqli($host, $user, $password, $dbname);\n\n    // Check connection\n    if ($conn->connect_error) {\n        die("Connection failed: " . $conn->connect_error);\n    }\n\n    return $conn;\n}\n\n// Establish connections to all databases\n$connections = [];\nforeach ($databases as $key => $dbDetails) {\n    $connections[$key] = connectToDatabase($dbDetails[0], $dbDetails[1], $dbDetails[2], $dbDetails[3]);\n}\n\n// Example queries\n$query1 = "SELECT * FROM table1";\n$query2 = "SELECT * FROM table2";\n\n// Execute queries\n$result1 = $connections['SGopen']->query($query1);\n$result2 = $connections['PTsecond']->query($query2);\n\n// Process results\nif ($result1 && $result1->num_rows > 0) {\n    while($row = $result1->fetch_assoc()) {\n        // Process $row\n    }\n}\n\nif ($result2 && $result2->num_rows > 0) {\n    while($row = $result2->fetch_assoc()) {\n        // Process $row\n    }\n}\n\n// Close connections\nforeach ($connections as $conn) {\n    $conn->close();\n}