<?php\n// Start the session\nsession_start();\n\n// Check if the counter is set in the session\nif (isset($_SESSION['page_counter'])) {\n    // Increment the counter\n    $_SESSION['page_counter'] += 1;\n} else {\n    // Initialize the counter\n    $_SESSION['page_counter'] = 1;\n}\n\n// Output the current counter value\necho "You have visited this site " . $_SESSION['page_counter'] . " times.";\n\n// Perform an action when the counter reaches a specific value, e.g., 10\nif ($_SESSION['page_counter'] == 10) {\n    echo "You have visited 10 pages!";\n    // Reset the counter if needed\n    // $_SESSION['page_counter'] = 0;\n}\n?>