// Controller\n<?php\nclass SessionController extends Zend_Controller_Action\n{\n    protected $session;\n\n    public function init() //Like a constructor\n    {\n        $this->_helper->viewRenderer->setNoRender(); // Will not automatically go to views/Session\n        $this->_helper->getHelper('layout')->disableLayout(); // Will not load the layout\n    }       \n\n    public function preDispatch() //Invokes code before rendering.  Good for sessions/cookies etc.\n    {\n        $this->session = new Zend_Session_Namespace();\n        if (!isset($this->session->messages)) {\n            $this->session->messages = []; // Initialize messages array if it doesn't exist\n        }\n    }\n\n    public function printthingAction()\n    {\n        $message = $this->getRequest()->getParam('textme');\n        $this->session->messages[] = $message;\n\n        // Use the view helper directly without storing it in the session\n        $viewHelper = new App_View_Helper_Tabbedbox();\n        echo $viewHelper->tabbedbox($this->session->messages);\n    }\n}\n?>