<?php\n$result = mysql_query("SELECT * FROM posts WHERE userid = '$user_id' ORDER BY DATE desc LIMIT 5") or die(mysql_error());\n\n$p = ''; // Initialize the $p variable\nwhile ($row = mysql_fetch_array($result)) {\n    // Escape the source value to be safely used in JavaScript\n    $source = addslashes($row['source']);\n\n    $p .= '<div id="red-div">\n                <div id="smartass">\n                    <div id="image">\n                        <img src="thumbs/' . $user_image . '" />\n                    </div>\n                    <div id="playsong">\n                        <a href="#" onclick="playsong(\'' . $source . '\'); return false;">\n                            <img src="play.png" width="16" height="16" border="0" />\n                        </a>' . $row['artist'] . ' - ' . $row['title'] . '\n                    </div>\n                </div>\n                <div id="post-comment">' . $row['comment'] . '</div>\n                <div id="post-date">' . $row['date'] . '</div>\n            </div>\n            <div id="dotted-line"></div>';\n}\n\necho '<script type="text/javascript">\n        parent.document.getElementById("posts").innerHTML = ' . json_encode($p) . ';\n      </script>';\n?>