<?php\nheader('Content-type: image/jpeg');\n\n// Load the image\n$im = imagecreatefromjpeg('test.jpg');\n\n// Apply grayscale filter\nimagefilter($im, IMG_FILTER_GRAYSCALE);\n\n// Increase contrast\nimagefilter($im, IMG_FILTER_CONTRAST, 255);\n\n// Negate the image\nimagefilter($im, IMG_FILTER_NEGATE);\n\n// Apply colorize filter to make the face orange\nimagefilter($im, IMG_FILTER_COLORIZE, 252, 137, 36);\n\n// Function to swap white shades to black shades\nfunction swapWhiteToBlack($image) {\n    $width = imagesx($image);\n    $height = imagesy($image);\n\n    for ($x = 0; $x < $width; $x++) {\n        for ($y = 0; $y < $height; $y++) {\n            $rgb = imagecolorat($image, $x, $y);\n            $colors = imagecolorsforindex($image, $rgb);\n\n            // Check if the color is close to white\n            if ($colors['red'] > 200 && $colors['green'] > 200 && $colors['blue'] > 200) {\n                $black = imagecolorallocate($image, 0, 0, 0);\n                imagesetpixel($image, $x, $y, $black);\n            }\n        }\n    }\n}\n\n// Swap white shades to black shades\nswapWhiteToBlack($im);\n\n// Output the image\nimagejpeg($im);\nimagedestroy($im);\n?>