class Database {\n    public function getQuery($query) {\n        // Your query logic here\n        echo "Querying: " . $query . "\n";\n    }\n}\n\nclass Member {\n    public function lastRegistered() {\n        // Your logic to get the last registered member here\n        echo "Getting last registered member\n";\n    }\n}\n\nclass Chesterx {\n    protected $database;\n    protected $member;\n\n    public function __construct() {\n        $this->database = new Database();\n        $this->member = new Member();\n    }\n}\n\nclass Hello extends Chesterx {\n    public function ornekFunc() {\n        $this->database->getQuery('popularNews');\n        $this->member->lastRegistered();\n    }\n}\n\n$hello = new Hello();\n$hello->ornekFunc();