$uplfile = $_FILES['uploadfile']['name'];   \n$upltmp = $_FILES["uploadfile"]["tmp_name"];\n\nif (!empty($uplfile)) {       \n    $ext = pathinfo($uplfile, PATHINFO_EXTENSION);\n    $upload_date = date("Y_m_d"); // Use this variable to change file name to avoid conflict with same name files\n    $upload_dir = "upload/";\n\n    // Ensure upload directory exists\n    if (!is_dir($upload_dir)) {\n        mkdir($upload_dir, 0777, true);\n    }\n\n    $file_name = pathinfo($uplfile, PATHINFO_FILENAME) . "_" . $upload_date . "_" . rand(0, getrandmax()) . "." . $ext;\n\n    // Move uploaded file\n    if (move_uploaded_file($upltmp, $upload_dir . $file_name)) {\n        echo "File uploaded successfully!";\n    } else {\n        echo "Failed to upload file.";\n    }\n} else {\n    echo "No file selected.";\n}