// Sample input array\n$inputArray = [\n    'http://i41.tinypic.com/3342r93.jpg',\n    'http://i41.tinypic.com/28cfub7.jpg',\n    'http://i41.tinypic.com/12dsa32.jpg'\n];\n\n// Sample database lookup function\nfunction checkIfExistsInDatabase($url) {\n    // Mock database results\n    $database = [\n        'http://i41.tinypic.com/28cfub7.jpg' => [\n            'id' => 44,\n            'name' => '28cfub7.jpg',\n            'size' => 105544,\n            'descr' => '',\n            'password' => '',\n            'delete_id' => 'D392',\n            'upload_id' => '6676FD881'\n        ]\n    ];\n\n    return $database[$url] ?? null;\n}\n\n// Initialize the output array\n$outputArray = [];\n\n// Iterate over the input array\nforeach ($inputArray as $index => $url) {\n    // Check if the URL exists in the database\n    $fileData = checkIfExistsInDatabase($url);\n    if ($fileData) {\n        // Add the file data to the output array at the correct index\n        $outputArray[$index] = $fileData;\n    } else {\n        // If the file does not exist in the database, you can handle the upload process here\n        // For demonstration, we'll just create a mock file data\n        $mockUploadData = [\n            'id' => 45,\n            'name' => basename($url),\n            'size' => 49000, // Mock size\n            'descr' => '',\n            'password' => '',\n            'delete_id' => '54C9', // Mock delete ID\n            'upload_id' => 'A58614C01' // Mock upload ID\n        ];\n\n        // Add the mock upload data to the output array at the correct index\n        $outputArray[$index] = $mockUploadData;\n    }\n}\n\n// Reindex the output array to maintain order\n$outputArray = array_values($outputArray);\n\n// Print the output array\nprint_r($outputArray);