<?php\n// Define an array of expected checkbox names\n$checkboxes = ['postAddress', 'displayPhone', 'student'];\n\n// Initialize each checkbox variable to a default value (e.g., 0 for unchecked)\nforeach ($checkboxes as $checkbox) {\n    $_POST[$checkbox] = isset($_POST[$checkbox]) ? 1 : 0;\n}\n\n// Now you can safely use $_POST['postAddress'], $_POST['displayPhone'], $_POST['student'] without undefined index errors\n$postAddress = $_POST['postAddress'];\n$displayPhone = $_POST['displayPhone'];\n$student = $_POST['student'];\n\n// Further processing of form data\necho "Post Address: " . $postAddress . "<br>";\necho "Display Phone: " . $displayPhone . "<br>";\necho "Student: " . $student . "<br>";\n?>