function set(&$x, &$y) {\n    $x = &$y[2]; // Set $x as a reference to $y[2]\n}\n\n$y = array(\n    0,\n    1,\n    array('something')\n);\n\n$x = array('old_val');\nprint "1. inited, x is:\n";\nprint_r($x);\n\nset($x, $y);\n\nprint "\n2. now, x is: ";\nprint_r($x);\n\n// Modify $x to check if $y[2] is also modified\n$x[] = 'new_val';\n\nprint "\n3. after modification, x is: ";\nprint_r($x);\n\nprint "\n4. y[2] is now: ";\nprint_r($y[2]);