<?php\n// Assuming you have a MySQL connection established and a query executed\n$results = $mysqli->query("SELECT id, model_name, unit_price FROM widgets WHERE model_name LIKE '%search_term%'");\n\necho '<form method="post" action="add_to_cart.php">';\nwhile ($row = $results->fetch_assoc()) {\n    echo '<div>';\n    echo '<span>' . htmlspecialchars($row['model_name']) . ' - $' . htmlspecialchars($row['unit_price']) . '</span>';\n    echo '<input type="number" name="quantities[' . htmlspecialchars($row['id']) . ']" min="0" placeholder="Quantity">';\n    echo '</div>';\n}\necho '<button type="submit">Add to Cart</button>';\necho '</form>';\n?>