<?php\n$options = [\n    '9999' => 'Tous',\n    '25' => '25',\n    '50' => '50',\n    '75' => '75'\n];\n\nfunction getSelectOptions($options, $selected) {\n    $output = '';\n    foreach ($options as $value => $label) {\n        $isSelected = ($value == $selected) ? 'selected="selected"' : '';\n        $output .= "<option value=\"" . get('index.php', $orgurl, 'nb_ligne=', '22', '23', $value) . "\" $isSelected>$label</option>";\n    }\n    return $output;\n}\n?>\n\n<tr class="ds_subsubhead_2">\n    <td colspan="21" align="left">\n        &nbsp;A&nbsp;\n        <select name="nb_linge" onChange="MM_jumpMenu('parent',this,0)" style="vertical-align:middle">\n            <?= getSelectOptions($options, $messagesParPage) ?>\n        </select>\n    </td>\n</tr>