<?php\nfunction generate_signed_url($s3, $bucket, $key, $expires) {\n    $cmd = $s3->getCommand('GetObject', [\n        'Bucket' => $bucket,\n        'Key' => $key\n    ]);\n\n    $request = $s3->createPresignedRequest($cmd, $expires);\n    return (string) $request->getUri();\n}\n\n// Example usage\nrequire 'vendor/autoload.php';\n\nuse Aws\S3\S3Client;\n\n$s3 = new S3Client([\n    'version' => 'latest',\n    'region'  => 'your-region',\n    'credentials' => [\n        'key'    => 'your-access-key',\n        'secret' => 'your-secret-key'\n    ]\n]);\n\n$bucket = 'your-bucket';\n$key = 'path/to/your/video.flv';\n$expires = '+20 minutes';\n\necho generate_signed_url($s3, $bucket, $key, $expires);\n?>