if ($_SERVER['REQUEST_METHOD'] === 'POST') {\n    $items = [];\n\n    if (!empty($_POST['first'])) {\n        $items[] = htmlspecialchars($_POST['first']);\n    }\n    if (!empty($_POST['second'])) {\n        $items[] = htmlspecialchars($_POST['second']);\n    }\n    if (!empty($_POST['third'])) {\n        $items[] = htmlspecialchars($_POST['third']);\n    }\n    if (!empty($_POST['fourth'])) {\n        $items[] = htmlspecialchars($_POST['fourth']);\n    }\n\n    $str = '';\n    $count = count($items);\n\n    if ($count > 1) {\n        $str = implode(', ', array_slice($items, 0, -1));\n        $str .= ' and ' . end($items) . '.';\n    } elseif ($count === 1) {\n        $str = $items[0] . '.';\n    }\n\n    echo $str;\n}