<?php\ndefine('CACHE_FILE', 'cache.txt');\ndefine('CACHE_INTERVAL', 86400); // 86400 seconds = 1 day\n\nfunction fetchRemoteFile() {\n    $remoteFileContent = file_get_contents('http://example.com/remote-file.txt');\n    file_put_contents(CACHE_FILE, $remoteFileContent);\n    return $remoteFileContent;\n}\n\nfunction getCachedFile() {\n    if (file_exists(CACHE_FILE)) {\n        $lastModified = filemtime(CACHE_FILE);\n        if ((time() - $lastModified) > CACHE_INTERVAL) {\n            // Cache is expired, fetch new content\n            return fetchRemoteFile();\n        } else {\n            // Return cached content\n            return file_get_contents(CACHE_FILE);\n        }\n    } else {\n        // Cache file doesn't exist, fetch new content\n        return fetchRemoteFile();\n    }\n}\n\n// Usage\n$content = getCachedFile();\necho $content;\n?>