<?php\n\nabstract class Animal {\n    protected $id;\n    protected $legs;\n    protected $height;\n\n    protected function __construct($id) {\n        $this->id = $id;\n    }\n\n    public static function create($type, $id) {\n        switch ($type) {\n            case 'dog':\n                return new Dog($id);\n            case 'cat':\n                return new Cat($id);\n            case 'mouse':\n                return new Mouse($id);\n            default:\n                throw new InvalidArgumentException("Unknown animal type");\n        }\n    }\n}\n\nclass Dog extends Animal {\n    public function __construct($id) {\n        parent::__construct($id);\n        $this->legs = 4;\n        $this->height = 1;\n    }\n}\n\nclass Cat extends Animal {\n    public function __construct($id) {\n        parent::__construct($id);\n        $this->legs = 4;\n        $this->height = 0.5;\n    }\n}\n\nclass Mouse extends Animal {\n    public function __construct($id) {\n        parent::__construct($id);\n        $this->legs = 4;\n        $this->height = 0.1;\n    }\n}\n\n// Usage\n$animal = Animal::create("dog", 12);\nvar_dump($animal);\n\n$animal2 = Animal::create("cat", 7);\nvar_dump($animal2);\n?>