<?php\nfunction copyImage($imageUrl, $saveTo) {\n    // Check if the URL is valid\n    if (filter_var($imageUrl, FILTER_VALIDATE_URL) === false) {\n        die('Invalid URL');\n    }\n\n    // Get the content of the image\n    $imageContent = file_get_contents($imageUrl);\n\n    // Check if the image content was successfully retrieved\n    if ($imageContent === false) {\n        die('Failed to download image');\n    }\n\n    // Define the local file path where the image will be saved\n    $savePath = $saveTo . basename($imageUrl);\n\n    // Save the image to the local file system\n    $saveResult = file_put_contents($savePath, $imageContent);\n\n    // Verify if the image was successfully saved\n    if ($saveResult === false) {\n        die('Failed to save image');\n    }\n\n    echo 'Image successfully copied to ' . $savePath;\n}\n\n// Example usage\n$imageUrl = 'http://www.mydomain.com/myimage.jpg';\n$saveTo = 'images/'; // Ensure this directory exists and is writable\ncopyImage($imageUrl, $saveTo);\n?>