<?php\n// Set the threshold for slow execution time in seconds\n$slow_threshold = 2.0; // 2 seconds\n\n// Capture the start time\n$start_time = microtime(true);\n\n// Your PHP code here...\n\n// Capture the end time\n$end_time = microtime(true);\n\n// Calculate execution time\n$execution_time = $end_time - $start_time;\n\n// Log if execution time exceeds the threshold\nif ($execution_time > $slow_threshold) {\n    $log_message = date('Y-m-d H:i:s') . " - Slow page execution detected: " . $execution_time . " seconds\n";\n    error_log($log_message, 3, '/path/to/your/log/file.log');\n}\n?>