<?php\n// Enable implicit flushing and disable output buffering\nini_set('output_buffering', 'off');\nini_set('zlib.output_compression', false);\nini_set('implicit_flush', true);\nob_implicit_flush(true);\nwhile (ob_get_level() > 0) {\n    ob_end_flush();\n}\n\n// Start your script\necho "<html><head><title>Progress Report</title></head><body>";\necho "<pre>";\n\n// Example loop to simulate repetitive work\n$totalTasks = 100;\nfor ($i = 1; $i <= $totalTasks; $i++) {\n    // Simulate work with sleep\n    sleep(1);\n    \n    // Send progress to the client\n    echo "Completed task $i of $totalTasks\n";\n    \n    // Flush output to client\n    flush();\n}\n\n// End your script\necho "</pre></body></html>";\n?>