$url = 'http://www.example.com/page.html';\n\n$curl = curl_init($url);\ncurl_setopt($curl, CURLOPT_RETURNTRANSFER, true);\ncurl_setopt($curl, CURLOPT_TIMEOUT, 10); // Set timeout duration in seconds\n\n$response = curl_exec($curl);\n$http_code = curl_getinfo($curl, CURLINFO_HTTP_CODE);\n$curl_errno = curl_errno($curl);\n\ncurl_close($curl);\n\nif ($response === false) {\n    // Check if the error is a timeout\n    if ($curl_errno == CURLE_OPERATION_TIMEOUTED) {\n        echo "Request timed out. Retrying...\n";\n        // Retry logic here\n    } else {\n        echo "cURL error: " . curl_strerror($curl_errno) . "\n";\n    }\n} else {\n    if ($http_code == 404) {\n        echo "Page not found (404). Adding to not found list.\n";\n        // Add to not found list logic here\n    } else {\n        echo "Page loaded successfully.\n";\n        // Process the loaded page here\n    }\n}