<?php\n// Database connection parameters\n$host = 'your_host';\n$dbname = 'your_database';\n$username = 'your_username';\n$password = 'your_password';\n\n// SQL file path\n$sqlFilePath = 'backup.sql';\n$gzipFilePath = 'backup.sql.gz';\n\n// Create the SQL dump command\n$command = "mysqldump --host=$host --user=$username --password=$password $dbname > $sqlFilePath";\nsystem($command, $output);\n\n// Open the SQL file and the GZIP file\n$sqlFile = fopen($sqlFilePath, 'r');\n$gzipFile = gzopen($gzipFilePath, 'w9'); // 'w9' sets the highest compression level\n\nif ($sqlFile && $gzipFile) {\n    // Write the SQL file content to the GZIP file\n    while (!feof($sqlFile)) {\n        $buffer = fread($sqlFile, 4096);\n        gzwrite($gzipFile, $buffer);\n    }\n\n    // Close the files\n    fclose($sqlFile);\n    gzclose($gzipFile);\n\n    // Optionally, delete the original SQL file\n    unlink($sqlFilePath);\n\n    echo "Backup completed and saved as $gzipFilePath";\n} else {\n    echo "Error in opening files.";\n}\n?>