use Zend\Form\Form;\nuse Zend\Form\Element\Csrf;\n\nclass MyForm extends Form\n{\n    public function __construct($name = null)\n    {\n        parent::__construct($name);\n\n        // Add CSRF protection\n        $this->add([\n            'type' => Csrf::class,\n            'name' => 'csrf',\n            'options' => [\n                'csrf_options' => [\n                    'timeout' => 600, // Token validity in seconds\n                ],\n            ],\n        ]);\n\n        // Add other form elements as needed\n    }\n}