<?php\n$mystring = "abcóüÚdefå";\nfile_put_contents($mystring, "");\n\n// Function to normalize strings to NFC\nfunction normalize_to_nfc($string) {\n    if (class_exists('Normalizer')) {\n        return Normalizer::normalize($string, Normalizer::FORM_C);\n    }\n    return $string;\n}\n\n$h = dir('.');\n$h->read(); // "."\n$h->read(); // ".."\n$filename = $h->read();\n\n$normalized_mystring = normalize_to_nfc($mystring);\n$normalized_filename = normalize_to_nfc($filename);\n\nprint "string: $normalized_mystring and filename: $normalized_filename are ";\n\nif ($normalized_mystring == $normalized_filename) print "equal\n";\nelse print "different\n";