function cleanHTML($html) {\n    // Remove control characters (0x00-0x1F except for newline, carriage return, and tab)\n    $cleaned = preg_replace('/[\x00-\x1F\x7F-\x9F]/u', '', $html);\n    return $cleaned;\n}\n\n$filename = 'test.html';\n\n// Read the HTML file\n$htmlContent = file_get_contents($filename);\n\n// Clean the HTML content\n$cleanedHtml = cleanHTML($htmlContent);\n\n// Load the cleaned HTML content into DOMDocument\n$dom = new DOMDocument();\n@$dom->loadHTML($cleanedHtml);\n\n// Continue processing with $dom as needed