if (is_uploaded_file(@$_FILES['ulimage']['tmp_name'])) {\n    $originalDir = dirname(__FILE__) . "/tmp/";\n    $thumbnailDir = $originalDir . "thumb/";\n    \n    // Ensure the thumbnail directory exists\n    if (!is_dir($thumbnailDir)) {\n        mkdir($thumbnailDir, 0755, true);\n    }\n    \n    $originalFilename = ImageHelper::treatFilename(uniqid() . "_" . $_FILES['ulimage']['name']);\n    $thumbnailFilename = ImageHelper::treatFilename(uniqid() . "_thumb_" . $_FILES['ulimage']['name']);\n    \n    move_uploaded_file($_FILES['ulimage']['tmp_name'], $originalDir . $originalFilename);\n    \n    ImageHelper::resizeImage($originalDir . $originalFilename, $thumbnailDir . $thumbnailFilename, $width, $height);\n}