<?php\n\nfunction generatePlayerStrengths($minStrength, $maxStrength, $numPlayers) {\n    $strengths = [];\n\n    // Loop through the range of strengths\n    for ($strength = $minStrength; $strength <= $maxStrength; $strength += 0.1) {\n        $strengths[round($strength, 1)] = 0;\n    }\n\n    // Total strength range\n    $totalRange = $maxStrength - $minStrength;\n\n    // Generate player strengths\n    for ($i = 0; $i < $numPlayers; $i++) {\n        // Use exponential distribution to skew higher strengths to be less common\n        $random = -$totalRange * log(1 - mt_rand() / mt_getrandmax());\n        $randomStrength = $minStrength + $random;\n        \n        // Ensure the random strength falls within the desired range\n        if ($randomStrength > $maxStrength) {\n            $randomStrength = $maxStrength;\n        } elseif ($randomStrength < $minStrength) {\n            $randomStrength = $minStrength;\n        }\n\n        // Round to 1 decimal place\n        $roundedStrength = round($randomStrength, 1);\n\n        // Increment count of players with this strength\n        $strengths[$roundedStrength]++;\n    }\n\n    // Sort strengths for output\n    ksort($strengths);\n\n    return $strengths;\n}\n\n$minStrength = 1.1;\n$maxStrength = 9.9;\n$numPlayers = 40000;\n\n$playerStrengths = generatePlayerStrengths($minStrength, $maxStrength, $numPlayers);\n\nforeach ($playerStrengths as $strength => $count) {\n    echo "Strength $strength: $count players\n";\n}\n?>