<?php\n// URL of the website providing the XML\n$url = 'http://example.com/data.xml';\n\n// Path where you want to save the XML file\n$localFilePath = 'local_copy.xml';\n\n// Fetch the XML content from the URL\n$xmlContent = file_get_contents($url);\n\nif ($xmlContent === FALSE) {\n    die('Error fetching XML content from the URL.');\n}\n\n// Save the XML content to a local file\n$result = file_put_contents($localFilePath, $xmlContent);\n\nif ($result === FALSE) {\n    die('Error saving XML content to the local file.');\n}\n\necho 'XML content successfully saved to ' . $localFilePath;\n?>