<?php\n// Define the original string\n$originalString = 'abcdefghi';\n\n// Function to generate a random string of specified length\nfunction generateRandomString($sourceString, $length) {\n    // Shuffle the source string to randomize its characters\n    $shuffledString = str_shuffle($sourceString);\n    \n    // Extract the required length of characters\n    $randomString = substr($shuffledString, 0, $length);\n    \n    return $randomString;\n}\n\n// Example usage: generate a random string of 6 characters\n$randomString = generateRandomString($originalString, 6);\necho $randomString;\n?>