// Assuming $response is the SimpleXML object returned from the Tarzan AWS toolkit\n\n// Access the QueryWithAttributesResult\n$queryResult = $response->QueryWithAttributesResult;\n\n// Loop through each Item\nforeach ($queryResult->Item as $item) {\n    echo "Item Name: " . $item->Name . "<br>";\n    \n    // Loop through each Attribute of the Item\n    foreach ($item->Attribute as $attribute) {\n        echo $attribute->Name . ": " . $attribute->Value . "<br>";\n    }\n    echo "<br>";\n}