<?php\n// Set the target date for the event\n$targetDate = '2023-12-25'; // Example: December 25, 2023\n\n// Get the current date\n$currentDate = date('Y-m-d');\n\n// Calculate the difference in days\n$date1 = new DateTime($currentDate);\n$date2 = new DateTime($targetDate);\n$interval = $date1->diff($date2);\n\n// Display the number of days remaining\necho '<blockquote>';\necho '<p>' . $interval->days . ' days to go for the great event</p>';\necho '</blockquote>';\n?>