<?php\n// Get the user input\n$clientName = isset($_POST['client_name']) ? $_POST['client_name'] : '';\n$cityId = isset($_POST['city_id']) ? $_POST['city_id'] : '';\n$business = isset($_POST['business']) ? $_POST['business'] : '';\n\n// Base SQL query\n$sql = "SELECT c.* FROM Clients c\n        LEFT JOIN Cities ci ON c.city_id = ci.id\n        WHERE 1=1";\n\n// Array to hold query parameters\n$params = [];\n$types = '';\n\n// Add conditions based on user input\nif (!empty($clientName)) {\n    $sql .= " AND c.name LIKE ?";\n    $params[] = '%' . $clientName . '%';\n    $types .= 's';\n}\n\nif (!empty($cityId)) {\n    $sql .= " AND ci.id = ?";\n    $params[] = $cityId;\n    $types .= 'i';\n}\n\nif (!empty($business)) {\n    $sql .= " AND c.business = ?";\n    $params[] = $business;\n    $types .= 's';\n}\n\n// Prepare and execute the query\n$conn = new mysqli($servername, $username, $password, $dbname);\n$stmt = $conn->prepare($sql);\n\nif (!empty($params)) {\n    $stmt->bind_param($types, ...$params);\n}\n\n$stmt->execute();\n$result = $stmt->get_result();\n\n// Fetch and display results\nwhile ($row = $result->fetch_assoc()) {\n    echo "Client: " . $row['name'] . " - Business: " . $row['business'] . " - City: " . $row['city_id'] . "<br>";\n}\n\n$stmt->close();\n$conn->close();\n?>