<?php\n// Initialize a new MagickWand resource\n$resource = NewMagickWand();\n\n// Read the image from the file path\nMagickReadImage($resource, $image);\n\n// Set the image compression quality\nMagickSetImageCompressionQuality($resource, 100);\n\n// Resize the image\nMagickResizeImage($resource, 660, 500, MW_LanczosFilter, 1);\n\n// Define the path to save the resized image\n$resizedImagePath = 'path/to/save/resized_image.jpg';\n\n// Write the resized image to the specified path\nMagickWriteImage($resource, $resizedImagePath);\n\n// Clear the MagickWand resource to free memory\nDestroyMagickWand($resource);\n?>