// Assuming $mdb2 is your MDB2 database object\n\n// Prepare the SQL query with placeholders\n$sql = "INSERT INTO profiles (email, fullName) VALUES (?, ?)";\n\n// Prepare the statement\n$stmt = $mdb2->prepare($sql, array('text', 'text'), MDB2_PREPARE_MANIP);\n\n// Check for errors in preparation\nif (PEAR::isError($stmt)) {\n    die($stmt->getMessage());\n}\n\n// Execute the statement with the actual values\n$result = $stmt->execute(array($email, $fullName));\n\n// Check for errors in execution\nif (PEAR::isError($result)) {\n    die($result->getMessage());\n}\n\n// Free the statement\n$stmt->free();