<?php\n// Assuming you have a database connection and have fetched your results into an array called $projects\n\n$projects = [\n    ['name' => 'Project 1', 'url' => 'detail/one'],\n    ['name' => 'Project 2', 'url' => 'detail/two'],\n    ['name' => 'Project 3', 'url' => 'detail/three'],\n    ['name' => 'Project 4', 'url' => 'detail/four'],\n    // Add more projects as needed\n];\n\n$ids = ['one', 'two', 'three', 'four', 'five', 'six', 'seven', 'eight', 'nine', 'ten', 'eleven', 'twelve'];\n\necho '<ul id="work_div">';\n$count = 0;\n\nforeach ($projects as $project) {\n    $id = isset($ids[$count]) ? $ids[$count] : 'default_id';\n    echo '<li id="' . $id . '"><a href="' . $project['url'] . '">' . $project['name'] . '</a></li>';\n    $count++;\n}\n\necho '</ul>';\n?>