$q4 = "SELECT domain_name, slots_config.bid \n       FROM slots_pid, slots_config, slots_sites \n       WHERE slots_config.bid=slots_pid.bid AND slots_sites.aid=slots_pid.aid";\n$result4 = mysql_query($q4);\n\n$resultarray = array();\n\nwhile($row = mysql_fetch_array($result4, MYSQL_ASSOC)) {\n    $resultarray[] = $row;\n}\n\n$found = false;\nforeach ($resultarray as $subArray) {\n    if (in_array("test", $subArray)) {\n        $found = true;\n        break;\n    }\n}\n\nif ($found) {\n    echo "Match found";\n} else {\n    echo "Match not found";\n}