function getTimezones() {\n       $timezones = DateTimeZone::listIdentifiers();\n       $timezone_offsets = [];\n       \n       foreach($timezones as $timezone) {\n           $datetime = new DateTime('now', new DateTimeZone($timezone));\n           $offset = $datetime->getOffset();\n           $offset_hours = $offset / 3600;\n           $offset_prefix = $offset_hours < 0 ? '-' : '+';\n           $offset_formatted = $offset_prefix . abs($offset_hours);\n           $timezone_offsets[$timezone] = "(UTC $offset_formatted) $timezone";\n       }\n       \n       return $timezone_offsets;\n   }\n   \n   // Example usage:\n   $timezones = getTimezones();\n   foreach ($timezones as $timezone => $name) {\n       echo "<option value=\"$timezone\">$name</option>";\n   }