// stream_audio.php\n\nsession_start();\n\n// Validate the token\nif (isset($_GET['token']) && $_GET['token'] === $_SESSION['audio_token']) {\n    $file = 'path/to/your/audiofile.mp3';\n\n    // Serve the MP3 file\n    header('Content-Type: audio/mpeg');\n    header('Content-Disposition: inline; filename="audiofile.mp3"');\n    readfile($file);\n} else {\n    // Invalid token\n    http_response_code(403);\n    echo 'Access denied.';\n}