$router = self::$frontController->getRouter();\n\n$baseRoute = new Zend_Controller_Router_Route(\n    ':module',\n    array('controller' => 'index', 'action' => 'index')\n);\n\n$controllerRoute = new Zend_Controller_Router_Route(\n    ':controller',\n    array('action' => 'index')\n);\n\n$idRoute = new Zend_Controller_Router_Route(\n    ':id',\n    null,\n    array('id' => '\d+')\n);\n\n$actionRoute = new Zend_Controller_Router_Route(\n    ':action'\n);\n\n$router->addRoute('shortcutOne', $baseRoute->chain($idRoute));\n$router->addRoute('shortcutTwo', $baseRoute->chain($controllerRoute)->chain($idRoute));\n$router->addRoute('shortcutThree', $baseRoute->chain($controllerRoute)->chain($actionRoute)->chain($idRoute));