function decrypt($encryptedText, $key) {\n    $cipher = "AES-128-ECB";\n    $decryptedText = openssl_decrypt(base64_decode($encryptedText), $cipher, $key, OPENSSL_RAW_DATA);\n    return $decryptedText;\n}\n\n$key = "1234567890123456"; // 16-byte key\n$encryptedText = "Base64_Encoded_Encrypted_Text_Here";\n$decryptedText = decrypt($encryptedText, $key);\necho "Decrypted Text: " . $decryptedText;