<?php\nfunction extractID($youtubeURL)\n{\n    // Split off the final bit of the URL beginning with ‘=’\n    $youtubeID = strchr($youtubeURL, '=');\n    // Remove that equals sign to get the video ID\n    $youtubeID = substr($youtubeID, 1);\n    return $youtubeID;\n}\n\n// Set feed URL\n$lang = $_POST['language'];\n$query = $_POST['query'] . "%20review";\nswitch ($lang) {\n    case "English":\n        $feedURL = "http://gdata.youtube.com/feeds/api/videos?q=" . urlencode($query) . "&v=2&format=5&lr=en";\n        break;\n    case "French":\n        $feedURL = "http://gdata.youtube.com/feeds/api/videos?q=" . urlencode($query) . "&v=2&format=5&lr=fr";\n        break;\n    case "Spanish":\n        $feedURL = "http://gdata.youtube.com/feeds/api/videos?q=" . urlencode($query) . "&v=2&format=5&lr=es";\n        break;                    \n}\n\n// Read feed into SimpleXML object\n$sxml = simplexml_load_file($feedURL);\n\n// Get the first media entry &\n// Get nodes in media: namespace for media information\n$media = $sxml->entry[0]->children('http://search.yahoo.com/mrss/');\n\n// Get video player URL\n$attrs = $media->group->player->attributes();\n// Extract the video's ID from the URL\n$videoURL = (string)$attrs['url'];\n$videoID = extractID($videoURL);\n\necho '<object width="425" height="350" data="http://www.youtube.com/v/' . $videoID . '" type="application/x-shockwave-flash"><param name="src" value="http://www.youtube.com/v/' . $videoID . '" /></object>';\n?>