$cacheKey = 'file_existence_' . md5(APPLICATION_PATH . '/controllers/company/LoginController.php');\n$fileExists = apc_fetch($cacheKey);\n\nif ($fileExists === false) {\n    // Check if the file exists for the first time\n    $fileExists = file_exists(APPLICATION_PATH . '/controllers/company/LoginController.php');\n    // Store the result in APC cache\n    apc_store($cacheKey, $fileExists);\n}\n\nif ($fileExists) {\n    include_once APPLICATION_PATH . '/controllers/company/LoginController.php';\n    if (class_exists("Company_LoginController")) {\n        echo 'customer-specific controller exists';\n    } else {\n        include_once APPLICATION_PATH . '/controllers/LoginController.php';\n        echo 'customer-specific controller does not exist; using default';\n    }\n} else {\n    include_once APPLICATION_PATH . '/controllers/LoginController.php';\n    echo 'customer-specific controller does not exist; using default';\n}