function convertToUserTimezone($gmtTime, $userTimezone) {\n       $dateTime = new DateTime($gmtTime, new DateTimeZone('UTC'));\n       $dateTime->setTimezone(new DateTimeZone($userTimezone));\n       return $dateTime->format('Y-m-d H:i:s');\n   }\n   \n   // Example usage:\n   $gmtTime = '2023-10-22 14:00:00';\n   $userTimezone = 'Asia/Kolkata';\n   $userTime = convertToUserTimezone($gmtTime, $userTimezone);\n   echo $userTime; // Outputs the converted time