function getFileUrl($filename) {\n    $baseUrl = "http://www.domain.com/";\n    $tempPath = "temp_content/";\n    $finalPath = "http://server1.domain.com/thumbs/";\n\n    $fileExists = file_exists($_SERVER['DOCUMENT_ROOT'] . '/' . $tempPath . $filename);\n    \n    if ($fileExists) {\n        return $baseUrl . $tempPath . $filename;\n    } else {\n        return $finalPath . $filename;\n    }\n}\n\n// Usage example\n$thumbnailUrl = getFileUrl('filename.jpg');