class MyClass {\n    private $connection;\n    private $params;\n    public $insertSuccess;\n\n    public function __construct($params) {\n        $this->params = $params;\n        $this->connectToDatabase();\n        $this->insertSuccess = $this->insertIntoDatabase();\n    }\n\n    private function connectToDatabase() {\n        // Database connection code here\n        // Example:\n        $this->connection = new mysqli('host', 'username', 'password', 'database');\n        if ($this->connection->connect_error) {\n            die("Connection failed: " . $this->connection->connect_error);\n        }\n    }\n\n    private function insertIntoDatabase() {\n        // Insert query code here\n        // Example:\n        $sql = "INSERT INTO my_table (column1, column2) VALUES ('value1', 'value2')";\n        if ($this->connection->query($sql) === TRUE) {\n            return true;\n        } else {\n            return false;\n        }\n    }\n}\n\n// Usage\n$foo = new MyClass('my params');\nif ($foo->insertSuccess) {\n    echo "Insert was successful!";\n} else {\n    echo "Insert failed!";\n}